import React from 'react'

export function TodoItem(props) {
    const onButtonPress = () => {
        props.eliminarCallback(props.id)
    }

    return (<li className={props.important?'bg-danger':''}>{props.titulo}
    <h3>{props.detalle}</h3>
                <button className=" btn btn-warning btn-sm align-self-end" onClick={onButtonPress}>Eliminar</button>
            </li>)
}
