import React, { Fragment, useRef, useState, useEffect } from "react";
import { TodoItem } from "./TodoItem";
import { v4 as uuid } from "uuid";

export function TodoList() {
    const [tareas, setTareas] = useState([]);
    const titulo = useRef();
    const important = useRef();
    const detalle =useRef();
    const KEY = "todolist-tareas";
    useEffect(() => {
        const tareasStorage = JSON.parse(localStorage.getItem(KEY));
        console.log(tareasStorage);
        setTareas((tareasAnteriores) => {
            return [...tareasAnteriores, ...tareasStorage];
        });
    }, []);
    useEffect(() => {
        localStorage.setItem(KEY, JSON.stringify(tareas));
    }, [tareas]);
    function eliminarTarea(id){
        console.log(id);
        setTareas((tareasAnteriores) => {            
            return tareasAnteriores.filter((item)=>item.id!==id);
        });
    }
    function agregarTarea() {
        const tittle = titulo.current.value;
        const value = detalle.current.value;
        const isImportant = important.current.checked;
        if (value === '') return;
        const nuevaTarea = {
            id: uuid(),
            important: isImportant,
            valor: value,
            titulo: tittle
        }
        setTareas((tareasAnteriores) => {
            return [...tareasAnteriores, nuevaTarea];
        });
    }
    return (
        <Fragment>
            <div className="container m-4 ">
                <h2>Listado de tareas</h2>
                <div className="col-md-12 m-2 row align-items-center">
                    <div className="input-group">
                        <input className="col-md-2 form-control" ref={titulo} type="text" placeholder="Ingrese un titulo" />                               
                        <textarea className="col-md-6 m-2 form-control" ref={detalle} placeholder="Ingrese el detalle de la tarea"></textarea>
                        <h6 className="col-md-1 m-2">Importante  <input  ref={important} type="checkbox" /> </h6>                                                    
                        <button onClick={agregarTarea} className="col-md-1 btn btn-success ms-2">Agregar</button>
                    </div>

                </div>
                <ul className="list-group col-md-3 row-md-3">
                    {tareas.map((item,i) => {
                      
                            return <TodoItem
                                        key={i}
                                        id={item.id}
                                        titulo={item.titulo}
                                        detalle={item.valor}
                                        important = {item.important}
                                        eliminarCallback={eliminarTarea}                                    
                                    >
                                    </TodoItem>
                        
                    }

                    )}
                </ul>
            </div>
        </Fragment>
    );
}
